msg.queryParameters = msg.queryParameters || {};

// get microtime
var timestamp = new Date();
const payload_obj = JSON.parse(msg.payload);
var payload = msg.payload;
if(payload_obj.timestamp) {
    timestamp = payload_obj.timestamp;
    delete payload_obj.timestamp;
    payload = JSON.stringify(payload_obj);
}

const topicArray= msg.topic.split("/");
const groupUid = topicArray[1].slice(6);
const deviceUid = topicArray[2].slice(7);
const topicUid = topicArray[3].slice(6);
const newTopic = topicArray.slice(2).join("/");

msg.queryParameters.param1 = groupUid;
msg.queryParameters.param2 = deviceUid;
msg.queryParameters.param3 = topicUid;
msg.queryParameters.param4 = newTopic;
msg.queryParameters.param5 = payload;
msg.queryParameters.param6 = timestamp;
msg.queryParameters.param7 = 0;
return msg;
